import React , {Component}from 'react';
import style from  './Board.module.css';
import Axios from 'axios';


class Board extends Component{
    state = {
        boardContents: {},
        isError : false
    }


    componentDidMount() {
        Axios.get('https://pro-organiser-app.firebaseio.com/boardContents/column.json')
            .then(response => {
                console.log(response);
                this.setState({
                    boardContents: response.data
                })
            })
            .catch(error => {console.log(error)});
    }

    onClick = () => this.props.history.push("/board");

     render(){
        let boardname = 
        Object.keys(this.state.boardContents).map(name => {
            let boardData = this.state.boardContents[name];
                return (

                    <button  className={style.boardsbutton} onClick={this.onClick} key={boardData.name}>
                    {boardData.name}
                    </button>

                )})

                return (
                    <div className="Boards">
                        <div className={style.BoardsTitle}>
                            <span>Boards</span>
                        </div>
                        <div className={style.BoardsName}>
                        {/* <NavLink to='/board'> */}
                            {boardname}
                            {/* </NavLink> */}
                        </div>
                        {/* <AppRouter/> */}
                    </div>
                 )

        }
}

export default Board;
