import React, { useState, useEffect } from "react";
import style from  './Board.module.css';
import Axios from 'axios';
import { Link } from "react-router-dom";
import Loader from "../../common/loader/Loader";

export const Board = (props) => {
    const name = props.match.params.boardName;

    const [columnData, setColumnData] = useState({});
    const [showColumn, setShowColumn] = useState(false);
    const [loading, setLoading] = useState(true);

    document.title = `${name} | Pro Organizer`;

    useEffect(() => {
        setShowColumn(false);
        getColumnData();
    }, [showColumn]);

    const getColumnData = () => {
        Axios
        .get(`https://pro-organiser-app.firebaseio.com/boardContents/${props.location.state.boardId}/column.json`)
        .then((response) => {
            setTimeout(setColumnData(response.data), 5000);
          setLoading(false);
        })
        .catch((err) => console.log(err));
    };

    return (
        <div>
          <br />
          {loading ? (
            <Loader></Loader>
          ) : (
              <div className={style.ColumnContainer}>
                  <div className={style.ColumnHeader}>
                    <div className={style.ColumnTitle}>
                        <span>{name}</span>
                    </div>
                    <div className={style.DeleteBoardBtn}>
                        <button className={style.DeleteBtn}>Delete Board</button>
                    </div>
                </div>
                <br />
                  <div className={style.BoardsData}>
                 { Object.entries(columnData).map((item) => (

                          <div className={style.BoardItem}>
                            <span className={style.BoardName}>
                               {item[1].name}
                            </span>
                          </div>

                      ))}
                  </div>
              </div>
          )}
        </div>
      );
    }


export default Board;
