import React , {Component}from 'react';
import style from  './Board.module.css';
import Axios from 'axios';


class Board extends Component{
    state = {
        column: {},
        isError : false
    }


    componentDidMount() {
        Axios.get('https://pro-organiser-app.firebaseio.com/boardContents/-M5MReGNu5ENFe8FCNzL/column.json')
            .then(response => {
                console.log(response);
                this.setState({
                    column: response.data
                })
            })
            .catch(error => {console.log(error)});
    }

    // onClick = () => this.props.history.push("/board");

     render(){
        let name = 
        Object.keys(this.state.column).map(name => {
            let boardData = this.state.column[name];
                return (

                    <button  className={style.boardsbutton} key={boardData.name}>
                    {boardData.name}
                    </button>

                )})

                return (
                    <div className="Boards">
                        <div className={style.BoardsTitle}>
                            <span>Boards</span>
                        </div>
                        <div className={style.BoardsName}>
                        {/* <NavLink to='/board'> */}
                            {name}
                            {/* </NavLink> */}
                        </div>
                        {/* <AppRouter/> */}
                    </div>
                 )

        }
}

export default Board;
