import React, { useState, useEffect } from "react";
import style from "./Card.module.css";
import Modal from './../../common/Modal/Modal';
import Axios from "axios";

function Card(props) {
  const [showModal, setShowModal] = useState(false);
  const { members, columnId, boardId, boardTitle, isCardDragged } = props;
  const [cardData, setCardData] = useState("");
  const [formComplete, setFormComplete] = useState(false);
  const [formIncompleteError, setFormIncompleteError] = useState(false);

  const AddCard = React.useRef();

  useEffect(() => {
    modalOpenHandler();
    getCardData();
  }, [showModal]);

  const modalOpenHandler = () => {
    let colBox = document.getElementById("AddCardBox");
    colBox.addEventListener("click", () => {
      setShowModal(true);
    });
  };

  const getCardData = () => {
    Axios
    .get(`https://pro-organiser-app.firebaseio.com/boardContents/${boardId}/column/${columnId}/card.json`)
    .then((response) => {
        setCardData(response.data);
    })
    .catch((error) => console.log(error));
};

  const handleAddCardClick = (event) => {
    event.preventDefault();
        if(AddCard.current.value.length === 0){
          setFormComplete(false);
          setFormIncompleteError(true);
    } else {
      // add column name in firebase
      Axios.post(`https://pro-organiser-app.firebaseio.com/boardContents/${props.boardId}/column.json`, {
        name: columnName,
      })
        .then((response) => {
          alert("column added succesfully");
          props.setShowColumn(true);
        })
        .catch((err) => console.log(err));

      setShowModal(false);
    }
  };


  const  modalCloseHandler = () => {setShowModal(false)};

  let modalContent = showModal ? 

  (
      <>
      {formIncompleteError ? <p style={{color : 'red'}}>Kindly complete the form before adding Column</p> : null}
      <div className={style.AddCardForm}>
        <h2>Add a Column</h2>
       <div className={style.FormInput}><span className={style.Lable}>Enter a column name:</span><input type="text" id="column_name" value={cardTitle} placeholder="Enter a column name" ref={AddCard} onChange={(e) => {e.preventDefault(); setColumnName(e.target.value);}}/></div>
      </div>
      <button className={style.AddButton} id="CreateColumn" disabled={formComplete} onClick={handleAddCardClick}>Add Column</button>
      </>
  )
  :null;

  return (
    <div>
      <div className={style.CardData}>
      {cardData &&
        Object.entries(cardData).map((item) => (
          <>
          <div className={style.CardName}>{item[1].cardTitle}</div>
          <div className={style.AddCardBox} id="AddCardBox">
            <button>Add a Card</button>
          </div>
          </>
         ) )}
      </div>
        <br />
        {showModal && (

            <Modal  
                content = {modalContent}
                close = {modalCloseHandler}/> 
        )}

    </div>
)
}

export default Card;
