import React , {Component}from 'react';
import style from  './AllBoards.module.css';
import Axios from 'axios';
import { Link } from 'react-router-dom';

class AllBoards extends Component{
    state = {
        boardContents: {},
        isError : false
    }


    componentDidMount() {
        Axios.get('https://pro-organiser-app.firebaseio.com/boardContents.json')
            .then(response => {
                // console.log(response);
                this.setState({
                    boardContents: response.data
                })
            })
            .catch(error => {console.log(error)});
    }

    // onClick = () => this.props.history.push(`/board/${boardData.id}`);

     render(){
        let boardname = 
        Object.keys(this.state.boardContents).map(boardname => {
            let boardData = this.state.boardContents[boardname];
                return (

                    // <button  className={style.boardsbutton} onClick={this.onClick} key={boardData.boardName}>
                    <Link to={`/board/${boardData.id}`}>{boardData.boardName}</Link> 
                    // </button>

                )})

                return (
                    <div className="Boards">
                        <div className={style.BoardsTitle}>
                            <span>Boards</span>
                        </div>
                        <div className={style.BoardsName}>
                            {boardname}
                        </div>
                    </div>
                 )

        }
}

export default AllBoards;
