import React , {Component}from 'react';
import './AllBoard.css';


class AllBoards extends Component{
    state = {
        boardContents: {},
        isError : false
    }


    componentDidMount() {
        Axios.get('https://pro-organiser-app.firebaseio.com/boardContents.json')
            .then(response => {
                console.log(response);
                this.setState({
                    boardContents: response.data
                })
            })
            .catch(error => {console.log(error)});
    }


     render(){
        let boardname = 
        Object.keys(this.state.boardContents).map(boardname => {
            let boardData = this.state.boardContents[boardname];
                        return (
                            <>
                            <li  key={boardData.boardName}> </li>
                            <li>{boardData.boardName}</li>
                            </>

                        )})

        return (
            <div className="MyStocks">
                <div className="Header">
                    <span className="MyStocksTitle">My Stocks</span>
                </div>
                <div className="body">

                        <ul>
                            {boardname}
                        </ul>

                    }
                </div>

            </div>
        )

}
}

export default AllBoards;
