import React, { Component } from 'react';
import './Modal.css';

import Backdrop from './../Backdrop/Backdrop';

class Modal extends Component {

    render() {
        var inlineStyles = {
            addColumn: {
              height: "250px",
            },
            addCard: {
              height: "600px",

            },
            showCard: {
              height: "450px",

            }
          };

        var style = props.flag==='addColumn'? inlineStyles.addColumn : (props.flag==='addCard'?inlineStyles.addCard:inlineStyles.showCard);
        return (
            <div>
                <Backdrop />
                <div className="modalbody">
                <button className="closebutton" onClick = {() => this.props.close(this.props.modalCloseHandler)}>X</button>
                    {this.props.content}
                </div>
            </div>
        )
    }
}

export default Modal;
