import React , {Component}from 'react';
import style from  './AllBoards.module.css';
import Axios from 'axios';
import {NavLink} from 'react-router-dom';
import AppRouter from '../../AppRouter';



class AllBoards extends Component{
    state = {
        boardContents: {},
        isError : false
    }


    componentDidMount() {
        Axios.get('https://pro-organiser-app.firebaseio.com/boardContents.json')
            .then(response => {
                // console.log(response);
                this.setState({
                    boardContents: response.data
                })
            })
            .catch(error => {console.log(error)});
    }


     render(){
        let boardname = 
        Object.keys(this.state.boardContents).map(boardname => {
            let boardData = this.state.boardContents[boardname];
                return (

                    <div  className={style.boardsbutton} key={boardData.boardName}>
                    {boardData.boardName}
                    </div>

                )})

                return (
                    <div className="Boards">
                        <div className={style.BoardsTitle}>
                            <span>Boards</span>
                        </div>
                        <div className={style.BoardsName}>
                        <NavLink to='/board'>
                            <div>boardname</div>
                            </NavLink>
                        </div>
                        <AppRouter/>
                    </div>
                 )

        }
}

export default AllBoards;
