import React, { useState, useEffect } from "react";
import style from "./Card.module.css";
import Modal from './../../common/Modal/Modal';
import Axios from "axios";

function Card(props) {
  const [showModal, setShowModal] = useState(false);
  const { members, columnId, boardId, boardTitle, isCardDragged } = props;
  const [cardData, setCardData] = useState("");

  const [cardTitle, setCardTitle] = useState("");
  const [team, setTeam] = useState([]);
  const [descrptn, setDescrptn] = useState("");
  const [dueDate, setDueDate] = useState("");

  const [formComplete, setFormComplete] = useState(false);
  const [formIncompleteError, setFormIncompleteError] = useState(false);

  // to split members list string into array
  const memberArr = members.split(",");

  const AddCard = React.useRef();

    // to get today's date iin yyyy-mm-dd format
    var today = new Date();
    var month,
      day = null;
    if (today.getMonth() < 10 || today.getDate() < 10) {
      month = "0" + (today.getMonth() + 1);
      day = "0" + today.getDate();
    }
    var date = today.getFullYear() + "-" + month + "-" + day;

  useEffect(() => {
    // modalOpenHandler();
    getCardData();
  }, [showModal]);


  const getCardData = () => {
    Axios
    .get(`https://pro-organiser-app.firebaseio.com/boardContents/${boardId}/column/${columnId}/card.json`)
    .then((response) => {
        setCardData(response.data);
    })
    .catch((error) => console.log(error));
};

  const handleAddCardClick = (event) => {
    event.preventDefault();
        if(AddCard.current.value.length === 0){
          setFormComplete(false);
          setFormIncompleteError(true);
    } else {
      // add column name in firebase
      Axios.post(`https://pro-organiser-app.firebaseio.com/boardContents/${props.boardId}/column.json`, {
        name: cardTitle,
      })
        .then((response) => {
          alert("column added succesfully");
          props.setShowColumn(true);
        })
        .catch((err) => console.log(err));

      setShowModal(false);
    }
  };

  const onSelectChange = (e) => {
    const values = [...e.target.selectedOptions].map((opt) => opt.value);
    setTeam(values);
  };

  const  modalCloseHandler = () => {setShowModal(false)};

  let modalContent = showModal ? 

  (
      <>
      {formIncompleteError ? <p style={{color : 'red'}}>Kindly complete the form before adding Card</p> : null}
      <div className={style.AddCardForm}>
        <h2>Add a Card</h2>
       <div className={style.FormInput}><span className={style.Lable}>Enter a title for your task:</span><input type="text" id="title" defaultValue={editDetails ? cardName : ""} placeholder="e.g. Add a new icon" onChange={(e) => setCardTitle(e.target.value)}/></div>
       <div className={style.FormInput}><span className={style.Lable}>Choose members for this task(select multiple,if needed):</span><select  id="membersList" name="membersList" defaultValue={editDetails ? teamDetail : ""} multiple onChange={onSelectChange}>{memberArr.map((item) => (<option value={item} key={item}>{item}</option>))}</select></div>
       <div className={style.FormInput}><span className={style.Lable}>Add the description for your task:</span><input id="description" type="text"  defaultValue={editDetails ? descDetails : ""} placeholder="Add your description here"  onChange={(e) => setDescrptn(e.target.value)}/></div>
       <div className={style.FormInput}><span className={style.Lable}>Select the due-date for this task:</span><input id="due_date" type="date" min={date} defaultValue={editDetails ? dateDetails : ""} onChange={(e) => setDueDate(e.target.value)}/></div>
      </div>
      <button className={style.AddButton} id="CreateColumn" disabled={formComplete} onClick={handleAddCardClick}>Add Column</button>
      </>
  )
  :null;

  return (
    <div>
      <div className={style.CardData}>
      {cardData &&
        Object.entries(cardData).map((item) => (
          <>
          <div className={style.CardInfo}>
          <div className={style.CardHeader}>
          <div className={style.CardName}>{item[1].cardTitle}</div>
          </div>
          <div className={style.CardIcon}>
          <div><i className="fas fa-list"></i></div>
          </div>
          </div>
          <div className={style.CardBtn}>
            <button className={style.AddCardBtn} onClick={() => setShowModal(true)}>Add a Card</button>
          </div>
          </>
         ) )}
      </div>
        <br />
        {showModal && (

            <Modal  
                content = {modalContent}
                close = {modalCloseHandler}/> 
        )}

    </div>
)
}

export default Card;
